import React from 'react';
import ReactDOM from 'react-dom';
//import './index.css';

 function init() {
    //  localStorage["Usersname"] = new Array();
    //  localStorage.Usersname[0] = prompt("A");
    //  var val = localStorage["Usersname"];
    //  document.querySelector("#result").innerHTML = val[0];
    localStorage.setItem('user', ["Taro", "Jiro", "Saburo"]);
 }
 class Page extends React.Component {
    // handleFormSubmit = () => {
    //     localStorage.setItem('re',["fe"]);
    //     localStorage.setItem('re',["fdd"]);
    // };

    renderOption () {
        var users = localStorage.getItem('user');
        var i = 0;
        return (
            <option>this.users[i]</option>
        );
    }

     render() {
        // this.handleFormSubmit();



         init();
         //const data = JSON.parse(localStorage.getItem('items'))
         //localStorage.getItem('re');

        //this.setState({users});
        var leftover = 4;
        var given = 5;
        var applause = 10;
        var time = "3/10";



       return (

         <div>
             <div className="user-info">
                <p>pic</p>
                <select name="example">
                    {this.renderOption}
                    <option value="sample1">{users[1]}</option>
                    <option value="sample2">{users[2]}</option>
                    <option value="sample3">{users[3]}</option>
                </select>
             </div>
             <div className="applause">
                <p>拍手できる: {leftover}</p>
                <p>拍手された: {given}</p>
             </div>
             <div className="cases">
                <p>pic</p>
                <select name="example">
                    <option value="sample1">{users[1]}</option>
                    <option value="sample2">{users[2]}</option>
                    <option value="sample3">{users[3]}</option>
                </select>
                <input></input>
                <button>投稿</button>
             </div>
            <div>
                <p>{users[1]} --> {users[2]}</p>
                <input></input>
                <button>hand</button>
                <p>{applause}</p>
                <p>{time}</p>
            </div>
         </div>
       );
     }
   }


 // =======================================
 ReactDOM.render(
     <Page />,
     document.getElementById('root')
   );
