import React from 'react';


import ReactDOM from 'react-dom';

// const options = [
//   { value: 'chocolate', label: 'Chocolate' },
//   { value: 'strawberry', label: 'Strawberry' },
//   { value: 'vanilla', label: 'Vanilla' }
// ]

// const MyComponent = () => (
//   <Select options={options} />
// )

function init() {
    localStorage.setItem(1, 'Taro');
    localStorage.setItem(2, 'Jiro');
    localStorage.setItem(3, 'Saburo');
}

class Body extends React.Component {

    state = {
        applauseleftover: 100,
        applausegiven: 5,
        applause: 10,
        posttime: "3/10",
        options : [{value: 1, label: 'Taro'}]
        //users: localStorage.getItem(1)
    }

    usernameOption () {
         var useroptions =  ['Taro', 'JIro'];
         var options = useroptions.map((n) => 
            <option>
                {n}
            </option>
         )
        var i = 0;
        return (
            <select>{options}</select>
        );
    }

    change  (event){
        this.setState({value: event.target.value});
    }

    render() {
        init();
        return (
        <div>
            <div className="user-info">
                <p>pic</p>
                {this.usernameOption}
            </div>
            <div className="applause">
                <MyApplauseLeftover applauseleftover={100 - this.state.applause}/>
                <p>拍手された: {this.state.applausegiven}</p>
            </div>
            <div className="cases">
                <p>pic</p>
                <select name="example">
                   {this.usernameOption}
                </select>
                <PostForm />
            </div>
            <div>
                <input></input>
                <MyApplauseButton applause="0"/>
                <p>{this.state.posttime}</p>
            </div>
         </div>
       );
     }
    }

class MyApplauseLeftover extends React.Component {
    constructor(props) {
        super(props);
        this.state = {leftover: this.props.applauseleftover};
    }

    render () {
        return (
            <p>拍手できる: {this.leftover}</p>
        )
    }
}

class MyApplauseButton extends React.Component {
    constructor(props) {
        super(props);
        this.state = {value: parseInt(this.props.applause)};
        this.buttonClicked = this.buttonClicked.bind(this);
    }

    buttonClicked(event) {
        if (this.state.value <= 14) {
            this.setState({value: this.state.value + 1});
        }
    }

    render() {
        return (
            <div>
                <button onClick={this.buttonClicked}>拍手</button>
                <div>{this.state.value}</div>
            </div>
        );
    }
}

class PostForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {value: ''};
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({value: event.target.value});
    }

    handleSubmit(event) {
        if (this.state.value.length > 5) {
            localStorage.setItem('post', this.state.value);
            //localStorage.setItem('time', );
        }
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <textarea value={this.state.value} onChange={this.handleChange} />
                <input type="submit" value="投稿" />
            </form>
        );
    }
}


// =======================================
ReactDOM.render(
    <Body />,
    document.getElementById('root')
);
