import React from 'react';
import ReactDOM from 'react-dom';
//import './index.css';

 function init() {
    //  localStorage["Usersname"] = new Array();
    //  localStorage.Usersname[0] = prompt("A");
    //  var val = localStorage["Usersname"];
    //  document.querySelector("#result").innerHTML = val[0];
    localStorage.setItem('user', ["Taro", "Jiro", "Saburo"]);
 }
 class Page extends React.Component {
    // handleFormSubmit = () => {
    //     localStorage.setItem('re',["fe"]);
    //     localStorage.setItem('re',["fdd"]);
    // };

    renderOption () {
        var users = localStorage.getItem('user');
        var i = 0;
        return (
            <option>{this.users}</option>
        );
    }

     render() {
        // this.handleFormSubmit();



         init();
         //const data = JSON.parse(localStorage.getItem('items'))
         //localStorage.getItem('re');

        //this.setState({users});
        var leftover = 100;
        var given = 5;
        var applause = 10;
        var time = "3/10";



       return (

         <div>
             <div className="user-info">
                <p>pic</p>
                <select name="example">
                    {this.renderOption}

                </select>
             </div>
             <div className="applause">
                <MyLeftover leftover={100-this.applause}/>
                <p>拍手された: {given}</p>
             </div>
             <div className="cases">
                <p>pic</p>
                <select name="example">
                   {this.renderOption}
                </select>
                <input></input>
                <button>投稿</button>
             </div>
            <div>

                <input></input>
                <MyButton applause="0"/>
                <p>{time}</p>
            </div>
         </div>
       );
     }
   }

   class MyLeftover extends React.Component {
       constructor(props) {
           super(props);
            this.state = {leftover: this.props.leftover};
       }

       render () {
           return (
                <p>拍手できる: {this.state.leftover}</p>
           )
       }
   }

   class MyButton extends React.Component {
    constructor(props) {
      super(props);
      //this.state = {value: 0};
      this.state = {value: parseInt(this.props.applause)};
      this.buttonClicked = this.buttonClicked.bind(this);
    }

    buttonClicked(event) {
        if (this.state.value <= 14) {
            this.setState({value: this.state.value+1});
        }
    }

    render() {
      return (
        <div>
            <button onClick={this.buttonClicked}>applause</button>
            <div>{this.state.value}</div>
        </div>
      );
    }
  }


 // =======================================
 ReactDOM.render(
     <Page />,
     document.getElementById('root')
   );
